ass="PropReview PropReview_<?php echo $review['bookingno']; ?>">                                    <div class="clear">                                        <div class="colLeft">                                            <div class="starRatingOuter1">                                                <div class="starRatingInner1"                                                     style="width: <?php echo $review['total_review'] * 20 ?>%;"></div>                                            </div>                                        </div>                                        <div class="colRight">                                            Booking No: <?= $review['bookingno']; ?>                                        </div>                                    </div>                                    <div class="content"><?php echo $review['review']; ?>                                    </div>                                    <div class="colRight">                                        <div class="email"><?php echo $review['email']; ?>                                            - <?php echo date('d-m-Y', strtotime($review['dateAdded'])); ?>                                        </div>                                    </div>                                </div>                                <?php endforeach;                            } ?>                        </div>                    </div>                </div>            </div>        </div>    </div>    <script>        $(document).on("click", ".toggleSchedule", function () {            $(this).next(".scheduleDetails").slideToggle();        });        function add_data(evt,type) {            var product_id = $(evt).attr('product_id');            var reviewer_id = $(evt).attr('reviewer_id');            var booking_no = $(evt).attr('booking_no');            var user_id = $(evt).attr('user_id');            var pro_title = $(evt).attr('pro_title');            var img_src = $(evt).attr('img_src');            $('#add_review #user_id').val(user_id);            $('#add_review #proid').val(product_id);            $('#add_review #booking_no').val(booking_no);            $('#add_review #booking_no_show').html(booking_no);            $('#add_review #pro_title').html(pro_title);            $('#add_review #pro_img').attr("src", img_src);            $('#add_review #type').val(type);        }        /*Create add review form values*/        function add_review() {            if ($('textarea#review-text-value').val() == '') {                $('#review-text-value').focus();                return false;            }            $('#reviewFormSubmit').submit();        }        /*Showing review*/        function booking_review(evt,type) {            var booking_no = $(evt).attr('booking_no');            var user_id = $(evt).attr('user_id');            $('.PropReview').hide();            if(type==1){            $('#owner_' + booking_no).show();        }else {            $('#listing_' + booking_no).show();        }        }        /*Show send message form*/        function post_discussion(evt) {            $('#rental_id').val($(evt).attr('product_id'));            $('#sender_id').val($(evt).attr('reviewer_id'));            $('#receiver_id').val($(evt).attr('renter_id'));            $('#booking_id').val($(evt).attr('booking_no'));        }    </script><?php$this->load->view('site/includes/footer');?>