m();        var length = value_str.length;        var maxlength = $(obj).attr('maxlength');        var id = obj.id;        var length = maxlength - length;        $('#' + id + '_char_count').text(length);    }</script><?php 				if ($this->lang->line('You can add') != '') {					$you_can= stripslashes($this->lang->line('You can add'));				} else {					$you_can= "You can add";				} 	?>	<input type="hidden" name="u_can" id="you_can" value="<?php echo $you_can; ?>"> <?php 				if ($this->lang->line('more_words') != '') {					$more_words= stripslashes($this->lang->line('more_words'));				} else {					$more_words= "more words";				} 	?>	<input type="hidden" name="" id="more_words" value="<?php echo $more_words; ?>">		<?php 				if ($this->lang->line('words_reached') != '') {					$words_reached= stripslashes($this->lang->line('words_reached'));				} else {					$words_reached= "Words Reached";				} 	?>	<input type="hidden" name="" id="words_reached" value="<?php echo $words_reached; ?>">	<script>    var wordLen1 = 8, len1;	var err1 = $("#you_can").val();	var err2 = $("#more_words").val();	var err3 = $("#words_reached").val();    $('#experience_title').keydown(function (event) {        len1 = $('#experience_title').val().split(/[\s]+/);        if (len1.length > wordLen1) {            if (event.keyCode == 46 || event.keyCode == 8) {                /*Allow backspace and delete buttons*/            }            else if (event.keyCode < 48 || event.keyCode > 57) { /*all other buttons*/                event.preventDefault();            }        }        wordsLeft = (wordLen1) - len1.length;        if (wordsLeft <= 0) {            document.getElementById("words_left_title").innerHTML = "8 " + err3;        }        else {            $("#words_left_title").html(err1 + " " + wordsLeft + " " + err2);        }    });</script>