<?php$this->load->view('site/includes/header');$disabled = 'false';$exp_type = $listDetail->row()->exp_type;?><div class="manage_items">    <div class="toggleMenuD">        <div class="bar1"></div>        <div class="bar2"></div>        <div class="bar3"></div>    </div>    <?php    $this->load->view('site/experience/experience_head_side');    ?>    <div class="centeredBlock">        <div class="content">            <h3><?php if ($this->lang->line('exp_maximum_number_ofGuests') != '') {                    echo stripslashes($this->lang->line('exp_maximum_number_ofGuests'));                } else echo "Maximum number of guests"; ?></h3>            <p><?php if ($this->lang->line('exp_num_of_guest_accommodate') != '') {                    echo stripslashes($this->lang->line('exp_num_of_guest_accommodate'));                } else echo "How Many number of guests you can accommodate?" ?></p>            <div>                <p class="text-danger error"                   style="display: none;"><?php if ($this->lang->line('exp_fill_all_fields') != '') {                        echo stripslashes($this->lang->line('exp_fill_all_fields'));                    } else echo "Please fill all mandatory fields"; ?></p>                <?php                echo form_open('#', array('onsubmit' => 'return validate_form_new(event)', 'name' => 'overviewlist', 'id' => 'overviewlist'));                echo form_input(array('type' => 'hidden', 'name' => 'experience_id', 'id' => 'experience_id', 'value' => $listDetail->row()->experience_id));                ?>                <div class="form-group">                    <p><?php if ($this->lang->line('group_size') != '') {                            echo stripslashes($this->lang->line('group_size'));                        } else echo "Group size"; ?><span class="impt"> *</span></p>                    <?= form_input('group_size', $listDetail->row()->group_size, array('id' => 'group_size', 'placeholder' => ($this->lang->line('group_size') != '') ? stripslashes($this->lang->line('group_size')) : "Group Size", 'onkeypress' => 'return isNumberKey(event);')); ?>                </div>                <div class="clear text-right">                    <?php                    echo form_submit('', ($this->lang->line('Save_and_Continue') != '') ? stripslashes($this->lang->line('Save_and_Continue')) : 'Save & Continue', array('class' => 'submitBtn1'));                    ?>                </div>                <?php                echo form_close();                ?>            </div>        </div>    </div>    <div class="rightBlock">        <h2><i class="fa fa-lightbulb-o"               aria-hidden="true"></i><?php if ($this->lang->line('experience_details_precise') != '') {                echo stripslashes($this->lang->line('experience_details_precise'));            } else echo "Be Precise"; ?></h2>        <p><?php if ($this->lang->line('Be_precise_about_the_number') != '') {                echo stripslashes($this->lang->line('Be_precise_about_the_number'));            } else echo "Be precise about the number of people who can accommodate your experience. Being transparent about the group size will help the guest know the quality of your service."; ?></p>            <!--  <p><?php if ($this->lang->line('exp_great_summary_prices') != '') {                echo stripslashes($this->lang->line('exp_great_summary_prices'));            } else echo "Make sure that you have entered a right count. Don’t forget to maintain the quality of service for each guest throughout the Experience. You don’t have to fill all of them. Experiences are meant to be social, so other travelers could join too."; ?></p> -->    </div></div><script>    /*Next button link*/    $(document).ready(function () {        $('#next-btn').click(function (e) {            window.location.href = '<?php echo base_url() . "price/" . $id; ?>';        });    });    /*Character count */    function isNumberKey(evt) {        var charCode = (evt.which) ? evt.which : event.keyCode        if (charCode > 31 && (charCode < 48 || charCode > 57))            return false;        return true;    }    /*Submitting Form*/    function validate_form_new(e) {        group_size = $('#group_size').val();        err = 0;        if (group_size == '') {            err = 1;        }        if (err == 1) {            $('.error').fadeIn('slow', function () {                $(this).delay(5000).fadeOut('slow');            });            return false;        } else {            $('.loading').show();            url = '<?php echo base_url() . "site/experience/add_group_size/" . $id;?>';            $('#overviewlist').attr('method', 'post');            $('#overviewlist').attr('action', url).submit();        }    }</script>