st_requirement, array('id' => 'guest_requirement', 'placeholder' => ($this->lang->line('guest_requirement') != '') ? stripslashes($this->lang->line('guest_requirement')) : "Guest requirement", 'onkeyup' => 'char_count(this)', 'maxlength' => 250)); ?>                    <p>                        <?php                        $string = str_replace(' ', '', $listDetail->row()->guest_requirement);                        $len = mb_strlen($string, 'utf8');                        $remaining = (250 - $len);                        ?>                        <span id="guest_requirement_char_count"><?= $remaining; ?></span> <?php if ($this->lang->line('exp_characters_remaining') != '') {                            echo stripslashes($this->lang->line('exp_characters_remaining'));                        } else echo "characters remaining"; ?></p>                     <!-- In Arabic dynamic --->                      <?php  foreach(language_dynamic_enable("guest_requirement", "") as $dynlang) { ?>                          <p><?php if ($this->lang->line('guest_requirement') != '') {                            echo stripslashes($this->lang->line('guest_requirement'));                        } else echo "Guest requirements"; ?>(<?php echo $dynlang[0]; ?>)<span class="impt"> *</span></p>                    <?= form_textarea($dynlang[1], $listDetail->row()->{$dynlang[1]}, array('id' => 'guest_requirement_ar', 'placeholder' => ($this->lang->line('guest_requirement') != '') ? stripslashes($this->lang->line('guest_requirement')) : "Guest requirement", 'onkeyup' => 'char_count(this)', 'maxlength' => 250)); ?>                    <p>                        <?php                        $string = str_replace(' ', '', $listDetail->row()->guest_requirement_ar);                        $len = mb_strlen($string, 'utf8');                        $remaining = (250 - $len);                        ?>                        <span id="guest_requirement_char_count"><?= $remaining; ?></span> <?php if ($this->lang->line('exp_characters_remaining') != '') {                            echo stripslashes($this->lang->line('exp_characters_remaining'));                        } else echo "characters remaining"; ?></p>                    <?php } ?>                </div>                <div class="clear text-right">                    <?php                    echo form_submit('', ($this->lang->line('Save_and_Continue') != '') ? stripslashes($this->lang->line('Save_and_Continue')) : 'Save & Continue', array('class' => 'submitBtn1'));                    ?>                </div>                <?php                echo form_close();                ?>            </div>        </div>    </div>    <div class="rightBlock">        <h2><i class="fa fa-lightbulb-o"               aria-hidden="true"></i><?php if ($this->lang->line('guest_requirement_transparent') != '') {                echo stripslashes($this->lang->line('guest_requirement_transparent'));            } else echo "Be Transparent"; ?></h2>        <p><?php if ($this->lang->line('List_the_requirements_which') != '') {                echo stripslashes($this->lang->line('List_the_requirements_which'));            } else echo "List the requirements which the guest should fulfill to be a part of your experience. "; ?></p>            <p><?php if ($this->lang->line('mention_about_age_limit') != '') {                echo stripslashes($this->lang->line('mention_about_age_limit'));            } else echo "<b>Example</b>: mention about age limit, who can take part in the experience, etc."; ?></p>    <!-- <p><?php if ($this->lang->line('exp_description_the_req_transparent') != '') {                echo stripslashes($this->lang->line('exp_description_the_req_transparent'));            } else echo "Always add a footnote to your guest. It helps them to make extra precautions. Add any age limits, certifications, or abilities required for your experience. Give a vivid description about your requirements to the guest which builds credibility in you. "; ?></p> -->    </div></div><script>    /*Next button link*/    $(document).ready(function () {        $('#next-btn').click(function (e) {            window.location.href = '<?php echo base_url() . "group_size/" . $id; ?>';        });    });    /*Character count */    function char_count(obj) {        value_str = obj.value.trim();        var length = value_str.length;        var maxlength = $(obj).attr('maxlength');        var id = obj.id;        var length = maxlength - length;        $('#' + id + '_char_count').text(length);    }    /*Submitting Form*/    function validate_form_new(e) {        guest_requirement = $('#guest_requirement').val();        guest_requirement_ar = $('#guest_requirement_ar').val();        err = 0;        if (guest_requirement == '') {            err = 1;        }        if (err == 1) {            $('.error').fadeIn('slow', function () {                $(this).delay(5000).fadeOut('slow');            });            return false;        } else {            $('.loading').show();            url = '<?php echo base_url() . "site/experience/add_guest_requirements/" . $id;?>';            $('#overviewlist').attr('method', 'post');            $('#overviewlist').attr('action', url).submit();        }    }</script>