charCode > 31 && (charCode < 48 || charCode > 57))            return false;        return true;    }    /*Submit form*/    function validate_form_new(e) {        experience_type = $('#experience_type').val();        num_err = $('#num_err').val();        type_id = $('#type_id').val();        err = 0;        if (experience_type != '') {            if (experience_type == 2) {                count = $('#total_count_time').val();            } else {                count = $('#total_count_date').val();                if (count < 2) {                    $('#days_error').html(num_err + " 2..!");                    return false;                } else {                    $('#days_error').html('');                }            }            if (count == '') {                err = 1;            }        } else {            err = 1;        }        if (type_id == '') {            err = 1;        }        if (err == 1) {            $('.error').fadeIn('slow', function () {                $(this).delay(5000).fadeOut('slow');            });            return false;        } else {            url = '<?php echo base_url() . "add_experience_new";?>';            $('#overviewlist').attr('method', 'post');            $('#overviewlist').attr('action', url).submit();        }    }    /*Next button link*/    $(document).ready(function () {        $('#next-btn').click(function (e) {            $('.loading').show();            window.location.href = '<?php echo base_url() . "experience_language_details/" . $id; ?>';        });    });    /*Update category*/    function UpdateExp_Category() {        var category = $("#type_id").val();        var bk_done = $("#bk_done").val();        var cat_updated = $("#cat_updated").val();        var exp_id = $("#id").val();        if (category != '' && exp_id != '') {            $.ajax({                type: 'POST',                data: {'category_id': category, 'exp_id': exp_id},                url: '<?= base_url(); ?>site/experience/UpdateExp_Category',                success: function (data) {                    data = data.trim();                    if (data == 'success') {                        $("#cat_update").html(cat_updated);                        $('#cat_update').fadeIn('slow', function () {                            $(this).delay(1000).fadeOut('slow', function () {                                $('#cat_update').html('');                            });                        });                    } else {                        org_type_id = $("#org_type_id").val();                        $("#type_id").val(org_type_id);                        $("#cat_update_error").html(bk_done);                        $('#cat_update_error').fadeIn('slow', function () {                            $(this).delay(1000).fadeOut('slow', function () {                                $('#cat_update_error').html('');                            });                        });                    }                }            });        } else {            $("#cat_update").html('<span style="color: red;">Please Choose Category..!</span>');            $('#cat_update').fadeIn('slow', function () {                $(this).delay(1000).fadeOut('slow', function () {                    $('#cat_update').html('');                });            });        }    }</script>