<?php$this->load->view('site/includes/header');$disabled = 'false';$exp_type = $listDetail->row()->exp_type;?><div class="manage_items">    <div class="toggleMenuD">        <div class="bar1"></div>        <div class="bar2"></div>        <div class="bar3"></div>    </div>    <?php    $this->load->view('site/experience/experience_head_side');    ?>    <div class="centeredBlock">        <div class="content">            <h3><?php if ($this->lang->line('exp_price_per_guest') != '') {                    echo stripslashes($this->lang->line('exp_price_per_guest'));                } else echo "Set a price per guest"; ?></h3>            <p><?php if ($this->lang->line('exp_price_experience') != '') {                    echo stripslashes($this->lang->line('exp_price_experience'));                } else echo "The price of your experience is entirely up to you. Play with the calculator to see how much you'd earn depending on the number of guests."; ?></p>            <div>                <p class="text-danger error"                   style="display: none;"><?php if ($this->lang->line('exp_fill_all_fields') != '') {                        echo stripslashes($this->lang->line('exp_fill_all_fields'));                    } else echo "Please fill all mandatory fields"; ?></p>                <?php                echo form_open('#', array('onsubmit' => 'return validate_form_new(event)', 'name' => 'overviewlist', 'id' => 'overviewlist'));                echo form_input(array('type' => 'hidden', 'name' => 'experience_id', 'id' => 'experience_id', 'value' => $listDetail->row()->experience_id));                ?>                <div class="form-group">                    <div class="col-md-4">                        <p><?php if ($this->lang->line('Currency') != '') {                                echo stripslashes($this->lang->line('Currency'));                            } else echo "Currency"; ?><span class="impt"> *</span></p>                        <?php                        $currencyArray = array();                        foreach ($currentCurrency_all as $currency) {                            $currencyArray[$currency->currency_type] = $currency->currency_type;                        }                        //print_r($currencyArray);                        echo form_dropdown('currency', $currencyArray, $listDetail->row()->currency, array('id' => 'currency'));                        ?>                    </div>                    <div class="col-md-8">                        <p><?php if ($this->lang->line('Price_per') != '') { echo stripslashes($this->lang->line('Price_per'));  } else { echo "Price Per Person"; } ?><span class="impt"> *</span></p>						<?php							echo '							<div class="input-group">							  <span class="input-group-addon currencySymb">$</span>';							echo form_input('price', ($listDetail->row()->price > 0) ? $listDetail->row()->price : '', array('id' => 'price', 'placeholder' => ($this->lang->line('Price') != '') ? stripslashes($this->lang->line('Price')) : "Price", 'onkeypress' => 'return isNumberKey(event);', 'maxlength' => 5));							echo '							</div>';						 ?>                        <br>                    </div>                </div>                <div class="clear text-right">                    <br>                    <br>                    <br>                    <br>                    <?php                    echo form_submit('', ($this->lang->line('Save_and_Continue') != '') ? stripslashes($this->lang->line('Save_and_Continue')) : 'Save & Continue', array('class' => 'submitBtn1'));                    ?>                </div>                <?php                echo form_close();                ?>            </div>        </div>    </div>    <div class="rightBlock">        <h2><i class="fa fa-lightbulb-o"               aria-hidden="true"></i><?php if ($this->lang->line('experience_details_price') != '') {                echo stripslashes($this->lang->line('experience_details_price'));            } else echo "Fix your price"; ?></h2>        <p><?php if ($this->lang->line('Fix_your_own_price') != '') {                echo stripslashes($this->lang->line('Fix_your_own_price'));            } else echo "Fix your own price, change the price at any time. You could calculate the cost of the material you provide, expenses involved in arranging the experience, any food expenses, etc. Make sure you provide a reasonable price which is affordable by the guest."; ?></p><!--  <p><?php if ($this->lang->line('exp_great_summary_price') != '') {                echo stripslashes($this->lang->line('exp_great_summary_price'));            } else echo "The price you set for your experience is up to you, and you can change it at any time. When setting your price, factor in things like the cost of any materials you need, the time you spend setting up, and the cost of food or rentals that you include for each guest. Consider calculating these expenses based on a full group of guests, as opposed to trying to calculate it for each individual guest. Make sure your price reflects the service you provide."; ?></p> -->    </div></div><script>    /*Next button link*/    $(document).ready(function () {        $('#next-btn').click(function (e) {            window.location.href = '<?php echo base_url() . "cancel_policy/" . $id; ?>';        });    });    /*Character count */    function isNumberKey(event) {        if (!((event.keyCode >= 65) && (event.keyCode <= 90) || (event.keyCode >= 97) && (event.keyCode <= 122) || (event.keyCode >= 48) && (event.keyCode <= 57))) {            event.returnValue = false;            return;        }        event.returnValue = true;    }    /*Submitting Form*/    function validate_form_new(e) {        price = $('#price').val();        currency = $('#currency').val();        err = 0;        if (price == '' || currency == 'Select Currency') {            err = 1;        }        if (err == 1) {            $('.error').fadeIn('slow', function () {                $(this).delay(5000).fadeOut('slow');            });            return false;        } else {            $('.loading').show();            url = '<?php echo base_url() . "site/experience/add_price/" . $id;?>';            $('#overviewlist').attr('method', 'post');            $('#overviewlist').attr('action', url).submit();        }    }	$("select[name='currency']").change(function(){		$.ajax({				type: 'POST',				url: '<?php echo base_url(); ?>site/Product/getCurrencySymbolByType',				data: {"currencyType": $(this).val()},				dataType: 'html',				beforeSend:function(){					$('.currencySymb').html('');				},				success: function (json) {					$('.currencySymb').html(json);				}			});	});	$(document).ready(function(){		$("select[name='currency']").trigger('change');	});</script>