lass="text-success text-center"><?php echo $this->session->flashdata('successMsg'); ?></p>                            <div class="formList">                                <label><?php if ($this->lang->line('OldPassword') != '') {                                        echo stripslashes($this->lang->line('OldPassword'));                                    } else echo "Old Password"; ?>:</label>                                <div class="right">																<?php if ($this->lang->line('Please_enter_your_old_password') != '') {                                        $pls_entr= stripslashes($this->lang->line('Please_enter_your_old_password'));                                    } else $pls_entr= "Please enter your old password"; ?>                                    <?php                                        echo form_password('old_password', '', array('placeholder' => "$pls_entr", 'id' => 'old_password'));                                    ?>                                </div>                            </div>                            <div class="formList">                                <label><?php if ($this->lang->line('NewPassword') != '') {                                        echo stripslashes($this->lang->line('NewPassword'));                                    } else echo "New Password"; ?>:</label>                                <div class="right">								<?php if ($this->lang->line('Please_enter_your_new_passowrd') != '') {                                        $new_pas= stripslashes($this->lang->line('Please_enter_your_new_passowrd'));                                    } else $new_pas= "Please enter your new passowrd"; ?>								                                    <?php                                        echo form_password('new_password', '', array('placeholder' => "$new_pas", 'id' => 'new_password'));                                    ?>                                </div>              